import streamlit as stimport pandas as pdimport preprocessing# Load the datatop_10 = pd.read_csv('top_10_teams.csv')df = pd.read_csv('df.csv')df['year'] = df['year'].astype(str)top_10['total_Runs'] = top_10['total_Runs'].astype(int)top_10['total_Wicket'] = top_10['total_Wicket'].astype(int)st.sidebar.title('IPL Analysis')user_menu = st.sidebar.radio(    'Select a page',    ['Overall Analysis', 'Team Analysis', 'Player Analysis'])# Overall Analysisif user_menu == 'Overall Analysis':    st.title('Overall Analysis')    st.write('---')    total_match = df.index.shape[0]    total_venue = df['venue'].unique().shape[0]    total_teams = df['team1'].unique().shape[0]    st.subheader('Top Statistics')    col1, col2, col3 = st.columns(3)    with col1:        with st.container(border=True):            st.header('Total Matches')            st.subheader(total_match)    with col2:        with st.container(border=True):            st.header('Total Venues')            st.subheader(total_venue)    with col3:        with st.container(border=True):            st.header('Total Teams')            st.subheader(total_teams)    # st.header('Participation of Teams')    preprocessing.particapation(df)    # st.header('Matches on Venue')    preprocessing.matches_on_venue(df)    # st.header('Most trophies won by a team')    preprocessing.winnerteam(df)    # st.header('Team TossWin-MatchWin Score')    preprocessing.Team_decision_score()    st.subheader('Top 10 Teams with Most Wins')    st.table(top_10)# Team Analysisif user_menu == 'Team Analysis':    st.title('Team Analysis')    st.write('---')    years = df['year'].unique().tolist()    years.insert(0, 'All')    years_selected = st.sidebar.selectbox('Select Year', years)    team = df['team1'].unique().tolist()    team.insert(0, 'All')    team_selected = st.sidebar.selectbox('Select Team', team)    preprocessing.team_analysis(years_selected, team_selected)    # Two Team Analysis    with st.container(border=True):        st.subheader('Team Comparison')        team.insert(0, 'Select Team')        team1 = st.selectbox('Select First Team', team)        team2 = st.selectbox('Select Second Team', team)        match, team1_win, team2_win = preprocessing.team_compared(team1, team2)        if team1 != 'Select Team' and team2 != 'Select Team':            col1, col2, col3 = st.columns(3)            with col1:                with st.container(border=True, height=250):                    st.header(team1,"Wins")                    st.subheader(team1_win)            with col2:                with st.container(border=True, height=250):                    st.header('Total Matches Played')                    st.subheader(match)            with col3:                with st.container(border=True, height=250):                    st.header(team2,"Wins")                    st.subheader(team2_win)    with st.container(border=True):        if team1 != 'Select Team' and team2 != 'Select Team':            st.header('Compared team by runs')            preprocessing.team_run_compared(team1, team2)# Player Analysisif user_menu == 'Player Analysis':    st.title('Player Analysis')    st.write('---')    preprocessing.top_10_batsman()    preprocessing.top_10_bowler()